import React, {Component, useState, useEffect} from 'react';
import {Container,Carousel,Button, Card} from 'react-bootstrap'
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {getItems} from '../../actions/fetchActions';




const ChosenForYou = ({getItems, fetchReducer }) => {

    useEffect (()=>{
        getItems();
    },[])

    const [index, setIndex] = useState(0);
    const [direction, setDirection] = useState(null);
  
    const handleSelect = (selectedIndex, e) => {
      setIndex(selectedIndex);
      setDirection(e.direction);
    };
    const itemsForYou = fetchReducer.slice(0,6).map(item=>(
        // <Carousel.Item>
        <Card style={{ width: '18rem' }} key={item.id}>
        <Card.Img src={item.url} />
        <Card.Body>
          <Card.Title>Outlander - cast away</Card.Title>
          <Card.Text>
            Outlander - cast away 2
          </Card.Text>
          <Button variant="primary">Go somewhere</Button>
        </Card.Body>
      </Card>
    //   </Carousel.Item>
    ));
    return (
        <Carousel activeIndex={index} direction={direction} onSelect={handleSelect}>
            {itemsForYou}
        </Carousel>
      );
}


ChosenForYou.propTypes = {
    getItems: PropTypes.func.isRequired,
    fetchReducer: PropTypes.array.isRequired
}

const mapStateToProps = state => ({
fetchReducer: state.fetchReducer.items
});

export default connect (mapStateToProps,{getItems})(ChosenForYou);
