import React, {Component, useState, useEffect} from 'react';
import {Container,Carousel,Button, Jumbotron,Card, CardGroup, Row, Col} from 'react-bootstrap'
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {getProductDetails} from '../../actions/fetchActions';
import { Route, Switch,Link } from 'react-router-dom';



const ProductDetails = ({getProductDetails, selectedProduct,match}) => {

    useEffect (()=>{
      var paramProduct = match.params.id
        getProductDetails(paramProduct);
    },[])

   

    const getProduct = (
      <>
        {/* Jumbotron */}
        <Jumbotron className="jumbotronSale" key={selectedProduct.id} fluid>
          <img
            src={selectedProduct.url}
            alt="image"
            width="100%"
            height="400px"
          />
        </Jumbotron>
        {/* Product title + Card Price with Basket */}
        <CardGroup>
          <Row className="col-12">
            {/* Title */}
            <div className=" col-4">
              <h5>Product Title</h5>
              <img src={selectedProduct.thumbnailUrl} 
              alt="thumbnail"
              style={{ width: "16rem", height: "4rem" }}
              />
            </div>
            {/* Price Card */}
            <div className=" cardPriceProduct col-5">
            <Card
              style={{height: "9rem" }}
              key={selectedProduct.id}
            >
              <Card.Body className="pl-0">
                <Card.Title>Outlander - cast away</Card.Title>
                <Button className="col-12 pl-0 pr-0" variant="success" size="sm">
                  ADD TO BASKET
                </Button>
                <Button className="col-12 pl-0 pr-0" variant="outline-success" size="sm">
                  ADD TO WISHLIST
                </Button>
              </Card.Body>
            </Card>
            </div>
          </Row>
        </CardGroup>
      </>
    );
  
    return (
      <>
      <div>
        {getProduct}
      </div>
      
     
        </>
      );
}


ProductDetails.propTypes = {
    getProductDetails: PropTypes.func.isRequired,
    selectedProduct: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
selectedProduct: state.fetchReducer.productDetails
});

export default connect (mapStateToProps,{getProductDetails})(ProductDetails);
