import {ADD_PRODUCT_TO_BASKET} from '../actions/types';

const initialState = {
    getBasketProducts:[]
}

export default function (state = initialState,action) {
    const {type,payload} = action;

    switch (type) {

        case ADD_PRODUCT_TO_BASKET:

            return{
                ...state,
                items: payload,
            }

        default:
            return state;
    }
}
