import React, {Component, useState, useEffect} from 'react';
//react-boottrap
import {Container,Carousel,Button, Card, CardGroup, Row} from 'react-bootstrap'
//redux
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {getItems} from '../../actions/fetchActions';
//assets
import infoIcon from "../../assets/infoIcon.png";
//router
import { Link } from 'react-router-dom';

const ChosenForYou = ({getItems, fetchReducer}) => {

    useEffect (()=>{
        getItems();
    },[]);

    const [index, setIndex] = useState(0);
    const [direction, setDirection] = useState(null);
  
    const handleSelect = (selectedIndex, e) => {
      setIndex(selectedIndex);
      setDirection(e.direction);
    };
    const itemsForYou = fetchReducer.slice(0,2).map(item=>(
      <Row className="col-12 col-md-6"  key={item.id}>
      <Card className="chosenForYouRow">
      <Link to={"/" + item.id}>
        <Card.Img variant="top"src={item.jumbotronUrl}/>
        </Link>
        <Card.Body>
    <Card.Title><div>{item.title}</div><p>€{item.price}</p></Card.Title>
        </Card.Body>
        <Card.Footer>
        <Link to={"/" + item.id}>
        <img
            className="infoIcon"
            src={infoIcon}
            alt="basket img"
            height="30px"
            width="30px"
          ></img>
          </Link>
        </Card.Footer>
      </Card>
      </Row>
    ));
    const itemsForYouTwo = fetchReducer.slice(2,4).map(item=>(
      <Row className="col-12 col-md-6" key={item.id}>
      <Card className="chosenForYouRow">
        <Link to={"/" + item.id}>
        <Card.Img variant="top"src={item.jumbotronUrl}/>
        </Link>
        <Card.Body>
    <Card.Title><div>{item.title}</div><p>€{item.price}</p></Card.Title>
        </Card.Body>
        <Card.Footer>
        <Link to={"/" + item.id}>
        <img
            className="infoIcon"
            src={infoIcon}
            alt="basket img"
            height="30px"
            width="30px"
          ></img>
          </Link>
        </Card.Footer>
      </Card>
      </Row>
    ));

    const itemsForYouThree = fetchReducer.slice(6,8).map(item=>(
      <Row className="col-12 col-md-6"  key={item.id}>
      <Card className="chosenForYouRow">
      <Link to={"/" + item.id}>
        <Card.Img variant="top"src={item.jumbotronUrl}/>
        </Link>
        <Card.Body>
    <Card.Title><div>{item.title}</div><p>€{item.price}</p></Card.Title>
        </Card.Body>
        <Card.Footer>
        <Link to={"/" + item.id}>
        <img
            className="infoIcon"
            src={infoIcon}
            alt="basket img"
            height="30px"
            width="30px"
          ></img>
          </Link>
        </Card.Footer>
      </Card>
      </Row>
    ));
    const itemsForYouFour = fetchReducer.slice(10,12).map(item=>(
      <Row className="col-12 col-md-6" key={item.id}>
      <Card className="chosenForYouRow">
        <Link to={"/" + item.id}>
        <Card.Img variant="top"src={item.jumbotronUrl}/>
        </Link>
        <Card.Body>
    <Card.Title><div>{item.title}</div><p>€{item.price}</p></Card.Title>
        </Card.Body>
        <Card.Footer>
        <Link to={"/" + item.id}>
        <img
            className="infoIcon"
            src={infoIcon}
            alt="basket img"
            height="30px"
            width="30px"
          ></img>
          </Link>
        </Card.Footer>
      </Card>
      </Row>
    ));
    return (
      <Container className="col-12" >
      <div className="subtitleLargest">
      <hr/>
        Chosen For You
      </div>
        <Carousel className="chosenForYouCarousel" indicators={false} activeIndex={index} direction={direction} onSelect={handleSelect}>
          <Carousel.Item>
            <CardGroup>
          {itemsForYou}
          {itemsForYouTwo}
          </CardGroup>
          </Carousel.Item>
          <Carousel.Item>
            <CardGroup>
          {itemsForYouThree}
          {itemsForYouFour}
          </CardGroup>
          </Carousel.Item>
        </Carousel>
        </Container>
      );
}


ChosenForYou.propTypes = {
    getItems: PropTypes.func.isRequired,
    fetchReducer: PropTypes.array.isRequired

}

const mapStateToProps = state => ({
fetchReducer: state.fetchReducer.items
});

export default connect (mapStateToProps,{getItems})(ChosenForYou);
