import React, {Component, useState, useEffect} from 'react';
import {Container,Carousel,Button, Card, CardGroup, Row, Col} from 'react-bootstrap'
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {getItems} from '../../actions/fetchActions';




const WeekOffer = ({getItems, fetchReducer }) => {

    useEffect (()=>{
        getItems();
    },[])

    const [index, setIndex] = useState(0);
    const [direction, setDirection] = useState(null);
  
    const handleSelect = (selectedIndex, e) => {
      setIndex(selectedIndex);
      setDirection(e.direction);
    };
    const itemsForYou = fetchReducer.slice(0,1).map(item=>(
        <Carousel.Item>
        <CardGroup>
          
            <Col className="colGroup col-3">
            <Row className=" rowGroup col-10">
        <Card className="cardGroup col-12" style={{ width: '16rem', height: '28rem'}} key={item.id}>
        <Card.Img 
          className="d-block w-100"
          src={item.url}
          height="350px" 
        src={item.url} />
        <Card.Body>
          <Card.Title>Outlander - cast away</Card.Title>
          <Button variant="primary" className=" lead float-right mr-3">Go somewhere</Button>
        </Card.Body>
      </Card>
      </Row>
      </Col>
      <Col className=" colGroup col-9 ">
      <Row className=" rowGroup col-12">
      <Card className="cardGroup col-4" style={{ width: '16rem', height: '13rem'}} key={item.id}>
        <Card.Img 
          className="d-block w-100"
          src={item.url}
          height="125px" 
        src={item.url} />
        <Card.Body>
          <Card.Title>Outlander - cast away</Card.Title>
          <Button variant="primary" className=" lead float-right mr-3">Go somewhere</Button>
        </Card.Body>
      </Card>
      <Card className="cardGroup col-4" style={{ width: '16rem', height: '13rem'}} key={item.id}>
        <Card.Img 
          className="d-block w-100"
          src={item.url}
          height="125px" 
        src={item.url} />
        <Card.Body>
          <Card.Title>Outlander - cast away</Card.Title>
          <Button variant="primary" className=" lead float-right mr-3">Go somewhere</Button>
        </Card.Body>
      </Card>
      <Card className="cardGroup col-4" style={{ width: '16rem', height: '13rem'}} key={item.id}>
        <Card.Img 
          className="d-block w-100"
          src={item.url}
          height="125px" 
        src={item.url} />
        <Card.Body>
          <Card.Title>Outlander - cast away</Card.Title>
          <Button variant="primary" className=" lead float-right mr-3">Go somewhere</Button>
        </Card.Body>
      </Card>
      <Card className="cardGroup col-4" style={{ width: '16rem', height: '13rem'}} key={item.id}>
        <Card.Img 
          className="d-block w-100"
          src={item.url}
          height="125px" 
        src={item.url} />
        <Card.Body>
          <Card.Title>Outlander - cast away</Card.Title>
          <Button variant="primary" className=" lead float-right mr-3">Go somewhere</Button>
        </Card.Body>
      </Card>
      <Card className="cardGroup col-4" style={{ width: '16rem', height: '13rem'}} key={item.id}>
        <Card.Img 
          className="d-block w-100"
          src={item.url}
          height="125px" 
        src={item.url} />
        <Card.Body>
          <Card.Title>Outlander - cast away</Card.Title>
          <Button variant="primary" className=" lead float-right mr-3">Go somewhere</Button>
        </Card.Body>
      </Card>
      <Card className="cardGroup col-4" style={{ width: '16rem', height: '13rem'}} key={item.id}>
        <Card.Img 
          className="d-block w-100"
          src={item.url}
          height="125px" 
        src={item.url} />
        <Card.Body>
          <Card.Title>Outlander - cast away</Card.Title>
          <Button variant="primary" className=" lead float-right mr-3">Go somewhere</Button>
        </Card.Body>
      </Card>
      </Row>
      </Col>
     
      </CardGroup>
      </Carousel.Item>
    ));
    return (
        <Carousel activeIndex={index} direction={direction} onSelect={handleSelect}>
            {itemsForYou}
        </Carousel>
      );
}


WeekOffer.propTypes = {
    getItems: PropTypes.func.isRequired,
    fetchReducer: PropTypes.array.isRequired
}

const mapStateToProps = state => ({
fetchReducer: state.fetchReducer.items
});

export default connect (mapStateToProps,{getItems})(WeekOffer);
