import React, {Component, useState, useEffect} from 'react';
import {Container,Carousel,Button, Jumbotron} from 'react-bootstrap'
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {getItems,getProductDetails} from '../../actions/fetchActions';
import { Route, Switch,Link } from 'react-router-dom';



const ProductDetails = ({getProductDetails, fetchReducer,match}) => {

    useEffect (()=>{
      var paramProduct = match.params.id
        getProductDetails(paramProduct);
        console.log(paramProduct,"props")
    },[])


    const getProduct = 
        fetchReducer.map(product=>(
          <Jumbotron className="jumbotronSale" key={product.id} fluid> 
               <img src={product.url} alt="image" width="100%" height="400px"/>
          </Jumbotron>
      ));
      


  
    return (
      <>
      ID: {match.params.id}
      <div>PRODUCT: 
        {getProduct}
        </div>
      
     
        </>
      );
}


// ProductDetails.propTypes = {
//     getProductDetails: PropTypes.func.isRequired,
//     fetchReducer: PropTypes.object.isRequired
// }

const mapStateToProps = state => ({
fetchReducer: state.fetchReducer.productDetails
});

export default connect (mapStateToProps,{getProductDetails})(ProductDetails);
