import React, {Component, useState, useEffect} from 'react';
//reactstrap
import {Container,Carousel,CarouselItem,Button, Card, CardGroup, Row, Col} from 'react-bootstrap'
//router
import {Link, Redirect} from 'react-router-dom';
//redux
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {getItems} from '../../actions/fetchActions';
import { addProductToBasket } from "../../actions/basketActions";
//assets
import promoCode from "../../assets/promoCode.png";
import basketicon from "../navbar/assets/basketicon.png";
import verticalCard1 from "../../assets/verticalCard1.jpg";
import verticalCard2 from "../../assets/verticalCard2.jpg";
import verticalCard3 from "../../assets/verticalCard3.jpg";
import infoIcon from "../../assets/infoIcon.png";





const ChosenPromotions = ({getItems, fetchReducer,addProductToBasket,basketReducer }) => {

    useEffect (()=>{
        getItems();
    },[])
    //Carousel
    const [index, setIndex] = useState(0);
    const [direction, setDirection] = useState(null);
  
    const handleSelect = (selectedIndex, e) => {
      setIndex(selectedIndex);
      setDirection(e.direction);
    };

    const addProduct = (product,basketReducer) => {
      addProductToBasket(product,basketReducer) 
      };
    
    //Toggle useState
    const [togglePromotion, setTogglePromotion] = useState(true);
    const [toggleWeekendOffer, setToggleWeekendOffer] = useState(false);
    const [toggleWeekOffer, setToggleWeekOffer] = useState(false);
 

    //TogglePromotion
    const onClickTogglePromotion = async () => {
    await setTogglePromotion(true)
    await setToggleWeekendOffer(false)
    await setToggleWeekOffer(false)
   }

   //ToggleWeekendOffer
   const onClickToggleWeekendOffer = async () => {
    await setTogglePromotion(false)
    await setToggleWeekendOffer(true)
    await setToggleWeekOffer(false)
   }

    //ToggleWeekOffer
    const onClickToggleWeekOffer = async () => {
      await setTogglePromotion(false)
      await setToggleWeekendOffer(false)
      await setToggleWeekOffer(true)
     }

  // CONDITIONALS FOR TOGGLE !

  //items promo fetch
if (togglePromotion) {

    const itemsForYou = fetchReducer.slice(0,4).map(item=>(
     <Col className="col-6">
      <Card key={item.id} className="chosenForYouRow">
        <Card.Img variant="top"src={item.jumbotronUrl}/>
        <Card.Body>
    <Card.Title><div>{item.title.slice(0,15)}...</div><p>${item.price}</p></Card.Title>
        </Card.Body>
        <Card.Footer>
          <img
                        src={infoIcon}
                        alt="basket img"
                        height="30px"
                        width="30px"
                      ></img>
        </Card.Footer>
      </Card>
      </Col>
      
    ));
    return (
      <>
      <hr/>
    <Row className="hideSetionMediaOne col-12">
      <Col className="col-8"></Col>
      <Col className="col-4">
        <Button className="transparentBtn" onClick={onClickTogglePromotion}>  <img id="promoCode"
                  src={promoCode}
                  alt="img"
                  height="30px"
                  width="30px"
                ></img>All Promotions</Button>
        <Button className="transparentBtn" onClick={onClickToggleWeekendOffer}><img id="promoCode"
                  src={promoCode}
                  alt="img"
                  height="30px"
                  width="30px"
                ></img>Weekend</Button>
        <Button className="transparentBtn" onClick={onClickToggleWeekOffer}><img id="promoCode"
                  src={promoCode}
                  alt="img"
                  height="30px"
                  width="30px"
                ></img>Week</Button>
                </Col>
      </Row>
      <Container className="col-12">
      <Row className="col-4">
            <Carousel
              activeIndex={index}
              direction={direction}
              onSelect={handleSelect}
            >
              {suggestedItems}
            </Carousel>
          </Row>
      <Row className="col-8">
          <CardGroup>
          {itemsForYou}
          </CardGroup>
  </Row>
        </Container>
        </>
    )
  } 

  
if (toggleWeekendOffer) {

  const itemsForYou = fetchReducer.slice(4,13).map(item=>(
   <Col className="col-4">
    <Card key={item.id} className="chosenForYouRow">
      <Card.Img variant="top"src={item.jumbotronUrl}/>
      <Card.Body>
  <Card.Title><div>{item.title.slice(0,16)}...</div><p>${item.price}</p></Card.Title>
      </Card.Body>
      <Card.Footer>
        <img
                      src={infoIcon}
                      alt="basket img"
                      height="30px"
                      width="30px"
                    ></img>
