import React, {Component, useState, useEffect} from 'react';
import {Container,Carousel,Button, Jumbotron,Card} from 'react-bootstrap'
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {getItems,getProductDetails} from '../../actions/fetchActions';
import { Route, Switch,Link } from 'react-router-dom';



const ProductDetails = ({getProductDetails, fetchReducer,match}) => {

    useEffect (()=>{
      var paramProduct = match.params.id
        getProductDetails(paramProduct);
    },[])


    const getProduct = (
          <Jumbotron className="jumbotronSale" key={fetchReducer99.id} fluid> 
          <Button primary>{fetchReducer99.title}</Button>
               <img src={fetchReducer99.url} alt="image" width="100%" height="400px"/>
          </Jumbotron>
      );
      


  
    return (
      <>
      ID: {match.params.id}
      <div>PRODUCT: 
        {getProduct}
        </div>
      
     
        </>
      );
}


ProductDetails.propTypes = {
    getProductDetails: PropTypes.func.isRequired,
    fetchReducer: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
fetchReducer69: state.fetchReducer.productDetails
});

export default connect (mapStateToProps,{getProductDetails})(ProductDetails);
