import React, {Component, useState, useEffect} from 'react';
//react-boottrap
import {Container,Carousel,Button, Card, CardGroup, Row} from 'react-bootstrap'
//redux
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {getItems} from '../../actions/fetchActions';
import { addProductToBasket } from "../../actions/basketActions";
//assets
import basketicon from "../navbar/assets/basketicon.png";
import infoIcon from "../../assets/infoIcon.png";
//router
import { Link } from 'react-router-dom';




const SimilarProducts = ({getItems, fetchReducer,addProductToBasket,basketReducer }) => {

    useEffect (()=>{
        getItems();
    },[])

    const addProduct = (product,basketReducer) => {
      addProductToBasket(product,basketReducer) 
      };

    const [index, setIndex] = useState(0);
    const [direction, setDirection] = useState(null);
  
    const handleSelect = (selectedIndex, e) => {
      setIndex(selectedIndex);
      setDirection(e.direction);
    };
    const itemsForYou = fetchReducer.slice(0,4).map(item=>(
      <Row className="col-xs-12 col-6 col-md-3">
      <Card key={item.id} className="chosenForYouRow">
      <Link to={"/" + item.id}>
        <Card.Img variant="top"src={item.jumbotronUrl}/>
        </Link>
        <Card.Body>
    <Card.Title><div>{item.title}</div><p>€{item.price}</p></Card.Title>
        </Card.Body>
        <Card.Footer>
        <Link to={"/" + item.id}>
        <img
            className="infoIcon"
            src={infoIcon}
            alt="basket img"
            height="30px"
            width="30px"
            onClick={() => addProduct(item, basketReducer)}
          ></img>
          </Link>
        </Card.Footer>
      </Card>
      </Row>
    ));
    const itemsForYouTwo = fetchReducer.slice(5,9).map(item=>(
      <Row className=" col-xs-12 col-6 col-md-3">
      <Card key={item.id} className="chosenForYouRow">
        <Link to={"/" + item.id}>
        <Card.Img variant="top"src={item.jumbotronUrl}/>
        </Link>
        <Card.Body>
    <Card.Title><div>{item.title}</div><p>€{item.price}</p></Card.Title>
        </Card.Body>
        <Card.Footer>
        <Link to={"/" + item.id}>
        <img
            className="infoIcon"
            src={infoIcon}
            alt="basket img"
            height="30px"
            width="30px"
            onClick={() => addProduct(item, basketReducer)}
          ></img>
          </Link>
        </Card.Footer>
      </Card>
      </Row>
    ));
    return (
      <Container className="col-12" >
      <div className="subtitleLargest">
      <hr/>
        Similar Products
      </div>
        <Carousel className="chosenForYouCarousel" indicators={false} activeIndex={index} direction={direction} onSelect={handleSelect}>
          <Carousel.Item>
            <CardGroup>
          {itemsForYou}
          </CardGroup>
          </Carousel.Item>
          <Carousel.Item>
            <CardGroup>
          {itemsForYouTwo}
          </CardGroup>
          </Carousel.Item>
        </Carousel>
        </Container>
      );
}


SimilarProducts.propTypes = {
    getItems: PropTypes.func.isRequired,
    addProductToBasket: PropTypes.func.isRequired,
    fetchReducer: PropTypes.array.isRequired,
    basketReducer: PropTypes.array.isRequired

}

const mapStateToProps = state => ({
fetchReducer: state.fetchReducer.items,
basketReducer: state.basketReducer.basketProducts
});

export default connect (mapStateToProps,{getItems,addProductToBasket})(SimilarProducts);
