import React, {Component, useState, useEffect} from 'react';
import {Container,Carousel,Button,Card,CardGroup, Modal} from 'react-bootstrap'
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {getItems, getProductDetails} from '../../actions/fetchActions';
import { Link } from 'react-router-dom';
import modalCarousel from './modalCarousel';




const ThumbnailCarousel = ({getItems,getProductDetails, fetchReducer,selectedProduct }) => {

    useEffect (()=>{
        getItems();
    },[])

  
    //modal button

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);

    const handleShow = (id) => {
          setShow(true)
          getProductDetails(id);
        
    };
    // carousel useState
    const [index, setIndex] = useState(0);
    const [direction, setDirection] = useState(null);
  
    const handleSelect = (selectedIndex, e) => {
      setIndex(selectedIndex);
      setDirection(e.direction);
    };

    const suggestedItems = fetchReducer.slice(0, 3).map(item => (
      <Carousel.Item>
        <CardGroup className="mb-3 pb-5">
          <Card className="cardGroup bg-dark text-white mb-3 mr-6" 
          style={{ width: '16rem', height: '6rem'}} key={item.id}>
              <Button variant="link" onClick={()=>handleShow(item.id)}>
            <Card.Img 
             className="d-block w-100"
             src={item.thumbnailUrl}
             height="125px"
             />
             </Button>
            <Card.ImgOverlay>
                Product title
            </Card.ImgOverlay>
          </Card>
          <Card className="cardGroup bg-dark text-white mb-3 mr-6" 
          style={{ width: '16rem', height: '6rem'}} key={item.id}>
              <Button variant="primary" onClick={()=>handleShow(item.id)}>
            <Card.Img 
             className="d-block w-100"
             src={item.thumbnailUrl}
             height="125px"
             />
             </Button>
            <Card.ImgOverlay>
                Product title
            </Card.ImgOverlay>
          </Card>
          <Card className="cardGroup bg-dark text-white mb-3 mr-6" 
          style={{ width: '16rem', height: '6rem'}} key={item.id}>
              <Button variant="link" onClick={()=>handleShow(item.id)}>
            <Card.Img 
             className="d-block w-100"
             src={item.thumbnailUrl}
             height="125px"
             />
             </Button>
            <Card.ImgOverlay>
                Product title
            </Card.ImgOverlay>
          </Card>
          <Card className="cardGroup bg-dark text-white mb-3 mr-6" 
          style={{ width: '16rem', height: '6rem'}} key={item.id}>
              <Button variant="link" onClick={()=>handleShow(item.id)}>
            <Card.Img 
             className="d-block w-100"
             src={item.thumbnailUrl}
             height="125px"
             />
             </Button>
            <Card.ImgOverlay>
                Product title
            </Card.ImgOverlay>
          </Card>
          <hr/>
        </CardGroup>
      </Carousel.Item>
    ));
    return (
      <>
      {/* modal */}
      <Modal show={show} 
        onHide={handleClose}
        >
            
            <Modal.Header 
            closeButton
            >
              <Modal.Body>
              <Card.Img 
             className="d-block w-100"
             src={selectedProduct.thumbnailUrl}
             height="125px"
             />
              </Modal.Body>
            </Modal.Header>
           
          </Modal>
          {/* carousel */}
        <Carousel activeIndex={index} direction={direction} onSelect={handleSelect}>
            {suggestedItems}
        </Carousel>
        </>
      );
}


ThumbnailCarousel.propTypes = {
    getItems: PropTypes.func.isRequired,
    getProductDetails: PropTypes.func.isRequired,
    fetchReducer: PropTypes.array.isRequired
}

const mapStateToProps = state => ({
fetchReducer: state.fetchReducer.items,
selectedProduct: state.fetchReducer.productDetails
});

export default connect (mapStateToProps,{getItems, getProductDetails})(ThumbnailCarousel);
