import React, {Component, useState, useEffect} from 'react';
//reactstrap
import {Container,Carousel,Button, Card, CardGroup, Row, Col} from 'react-bootstrap'
//router
import {Link, Redirect} from 'react-router-dom';
//redux
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {getItems} from '../../actions/fetchActions';
//assets
import promoCode from "../../assets/promoCode.png";
import basketicon from "../navbar/assets/basketicon.png";
import verticalCard1 from "../../assets/verticalCard1.jpg";
import verticalCard2 from "../../assets/verticalCard2.jpg";
import verticalCard3 from "../../assets/verticalCard3.jpg";





const ChosenPromotions = ({getItems, fetchReducer }) => {

    useEffect (()=>{
        getItems();
    },[])
    //Carousel
    const [index, setIndex] = useState(0);
    const [direction, setDirection] = useState(null);
  
    const handleSelect = (selectedIndex, e) => {
      setIndex(selectedIndex);
      setDirection(e.direction);
    };
    
    //Toggle useState
    const [togglePromotion, setTogglePromotion] = useState(true);
    const [toggleWeekendOffer, setToggleWeekendOffer] = useState(false);
    const [toggleWeekOffer, setToggleWeekOffer] = useState(false);
 

    //TogglePromotion
    const onClickTogglePromotion = async () => {
    await setTogglePromotion(true)
    await setToggleWeekendOffer(false)
    await setToggleWeekOffer(false)
   }

   //ToggleWeekendOffer
   const onClickToggleWeekendOffer = async () => {
    await setTogglePromotion(false)
    await setToggleWeekendOffer(true)
    await setToggleWeekOffer(false)
   }

    //ToggleWeekOffer
    const onClickToggleWeekOffer = async () => {
      await setTogglePromotion(false)
      await setToggleWeekendOffer(false)
      await setToggleWeekOffer(true)
     }


     
  // CONDITIONALS FOR TOGGLE !

  //items promo fetch
if (togglePromotion) {

    const itemsForYou = fetchReducer.slice(0,4).map(item=>(
      <Col className="col-4">
      <Card key={item.id} className="chosenForYCol">
        <Card.Img variant="top"src={item.jumbotronUrl}/>
        <Card.Body>
    <Card.Title><div>{item.title}</div><p>${item.price}</p></Card.Title>
        </Card.Body>
        <Card.Footer>
          <Button className="slideMainBtn"> <img
                        src={basketicon}
                        alt="basket img"
                        height="30px"
                        width="30px"
                      ></img></Button>
        </Card.Footer>
      </Card>
      </Col>
    ));
    return (
      <>
      <hr/>
      <Row className="hideSetionMediaOne col-6">
        <Button className="transparentBtn" onClick={onClickTogglePromotion}>  <img id="promoCode"
                  src={promoCode}
                  alt="img"
                  height="30px"
                  width="30px"
                ></img>All Promotions</Button>
        <Button className="transparentBtn" onClick={onClickToggleWeekendOffer}><img id="promoCode"
                  src={promoCode}
                  alt="img"
                  height="30px"
                  width="30px"
                ></img>Weekend</Button>
        <Button className="transparentBtn" onClick={onClickToggleWeekOffer}><img id="promoCode"
                  src={promoCode}
                  alt="img"
                  height="30px"
                  width="30px"
                ></img>Week</Button>
      </Row>
      <Container className="col-12">
      <Row className="col-6">
      <Card className="col-6">
        <Card.Img 
          src={verticalCard1}
          height="auto"
          width="auto"  
        />
        <Card.Body>
          <Card.Title>Outlander - cast away</Card.Title>
          <Button variant="primary" className=" lead float-right mr-3">Go somewhere</Button>
        </Card.Body>
      </Card>
      <Card className="col-6">
        <Card.Img 
          src={verticalCard2}
          height="auto"
          width="auto" 
        />
        <Card.Body>
          <Card.Title>Outlander - cast away</Card.Title>
          <Button variant="primary" className=" lead float-right mr-3">Go somewhere</Button>
        </Card.Body>
      </Card>
      </Row>
      <Row className="col-6">
      <Carousel className="hideSetionMediaOne" activeIndex={index} direction={direction} onSelect={handleSelect}>
      {itemsForYou}
  </Carousel>
  </Row>
        </Container>
        </>
    )
  } 
  //  //items weekOffer fetch
  //  if (toggleWeekOffer) {
  //   const itemsForYou = fetchReducer.slice(0,3).map(item=>(
  //     <Carousel.Item>
  //     <CardGroup>
        
  //         <Col className="colGroup col-3">
  //         <Row className=" rowGroup col-12">
