import React, {Component, useState, useEffect} from 'react';
import {Container,Carousel,Button,Card,CardGroup, Modal} from 'react-bootstrap'
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {getItems} from '../../actions/fetchActions';
import { Link } from 'react-router-dom';
import modalCarousel from './modalCarousel';




const ThumbnailCarousel = ({getItems,fetchReducer }) => {

    useEffect (()=>{
        getItems();
    },[])

    //modal button

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => {setShow(true)
    return (
        <Modal show={show} 
        // onHide={handleClose}
        >
            <Modal.Header 
            // closeButton
            >
              <Modal.Title>Modal heading</Modal.Title>
            </Modal.Header>
            <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" 
            //   onClick={handleClose}
              >
                Close
              </Button>
              <Button variant="primary" 
            //   onClick={handleClose}
              >
                Save Changes
              </Button>
            </Modal.Footer>
          </Modal>
    );
    }

    const [index, setIndex] = useState(0);
    const [direction, setDirection] = useState(null);
  
    const handleSelect = (selectedIndex, e) => {
      setIndex(selectedIndex);
      setDirection(e.direction);
    };

    const suggestedItems = fetchReducer.slice(0, 4).map(item => (
      <Carousel.Item>
        <CardGroup className="mb-3 pb-5">
          {/* <Card className="cardGroup" style={{ width: '16rem', height: '6rem'}} key={item.id}>
        <Card.Img 
          className="d-block w-100"
          src={item.thumbnailUrl}
          height="150px" 
        />
      </Card> */}
          <Card className="cardGroup bg-dark text-white mb-3 mr-6" 
          style={{ width: '16rem', height: '6rem'}} key={item.id}>
              <Button variant="link" onClick={handleShow}>
            <Card.Img 
             className="d-block w-100"
             src={item.thumbnailUrl}
             height="125px"
             />
             </Button>
            <Card.ImgOverlay>
                Product title
            </Card.ImgOverlay>
          </Card>
          <Card className="cardGroup bg-dark text-white mb-3 mr-6" 
          style={{ width: '16rem', height: '6rem'}} key={item.id}>
              <a variant="primary" onClick={handleShow}>
            <Card.Img 
             className="d-block w-100"
             src={item.thumbnailUrl}
             height="125px"
             />
             </a>
            <Card.ImgOverlay>
                Product title
            </Card.ImgOverlay>
          </Card>
          <Card className="cardGroup bg-dark text-white mb-3 mr-6" 
          style={{ width: '16rem', height: '6rem'}} key={item.id}>
            <Card.Img 
             className="d-block w-100"
             src={item.thumbnailUrl}
             height="125px"
             />
            <Card.ImgOverlay>
                Product title
            </Card.ImgOverlay>
          </Card>
          <Card className="cardGroup bg-dark text-white mb-3 mr-6" 
          style={{ width: '16rem', height: '6rem'}} key={item.id}>
            <Card.Img 
             className="d-block w-100"
             src={item.thumbnailUrl}
             height="125px"
             />
            <Card.ImgOverlay>
                Product title
            </Card.ImgOverlay>
          </Card>
          <hr/>
        </CardGroup>
      </Carousel.Item>
    ));
    return (
      <>
        <Carousel activeIndex={index} direction={direction} onSelect={handleSelect}>
            {suggestedItems}
        </Carousel>
        </>
      );
}


ThumbnailCarousel.propTypes = {
    getItems: PropTypes.func.isRequired,
    fetchReducer: PropTypes.array.isRequired
}

const mapStateToProps = state => ({
fetchReducer: state.fetchReducer.items
});

export default connect (mapStateToProps,{getItems})(ThumbnailCarousel);
